#!/bin/sh
CFLAGS="-std=c23 -Wall -Wextra -pedantic -fsanitize=address -fsanitize=undefined -Wfloat-equal -Wundef -Wshadow -Wcast-align -Wstrict-prototypes -Wwrite-strings -Wstrict-overflow=5 -Wcast-qual -Wswitch-enum -Wconversion -Wunreachable-code -Wpointer-arith -Wuninitialized -Winit-self -march=native -O3 -g -Wdouble-promotion -Wformat -Wformat-security -Wnull-dereference -Wimplicit-fallthrough=5 -Wunused -Wuse-after-free=3 -Wuseless-cast -Wstrict-overflow=5 -Wsuggest-attribute=cold -Wsuggest-attribute=pure -Wsuggest-attribute=const -Wsuggest-attribute=noreturn -Wmissing-noreturn -Wsuggest-attribute=malloc -Wsuggest-attribute=returns_nonnull"
clang $CFLAGS $@
